import axios from 'axios'

const GetMessageRequest = () => {return {type: 'GET_MESSAGE_REQUEST'}}
const GetMessageSuccess = (data) => {
    return{
        type : 'GET_MESSAGE_SUCCESS',
        payload : data
    }
}

const GetMessageError = (err) =>{
    return{
        type : 'GET_MESSAGE_ERROR',
        payload : err
    }
}

export const GetMessage = (userToken, id)=>{
    return (dispatch) => {
        dispatch(GetMessageRequest())
        return axios.get(`${process.env.REACT_APP_API_URL}message/?id_chatroom=${id}`,{
            headers: {
                'user-token': userToken
            }
        }).then((res) => {
            GetMessageSuccess(res.data.data)
        }).catch((err) => {
            GetMessageError(err.message)
        })   
    }
}
