import { Link } from 'react-router-dom'
import { i_bell } from '../../../images'
import Avatar from '../../../images/photos'
import axios from 'axios'
import React,{useState} from 'react'


const List = () =>{
    // state = {
    const [Data, setData] = useState(null)
    // }

    React.useEffect(()=>{
        axios.get('http://localhost:5024/api/v1/contacts?id=1').then((res) => {
            /* res.data.data.map((element)=>{
                setData(element.name.friend)}
            }) */
            /* console.log(res, 'ini datanya') */
            setData(res.data.data, Data)
        }).catch((err) => {
            console.log(err.message)
        })
    })
    return(
        <div className="list mb-5">
            {Data.map((element)=>(
            <Link to='#'  className='no-styling'>
                <div className="d-flex flex-row row my-3 me-0">
                    <div className="col-4 col-lg-4">
                        <img src={Avatar.photo1} className="img-fluid" alt="..." />
                    </div>
                    <div className="col-8 col-lg-8">
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-2">
                                <div className="d-flex flex-row">
                                    <span className="fw-bold text-lg-start">{element.name} 
                                        {/* {element.icon} */}
                                    </span>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    <small className="text-muted text-lg-start mb-1">{/* {element.time} */}</small>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-1">
                                <div className="d-flex flex-row">
                                    <small className="text-purple text-sm-start">{/* {element.newMessage} */}</small>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    {/* {element.count} */}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Link>
            )
            )}
      </div>
    )
}

export default List
