import {i_eye, i_google}  from '../images'
import axios from 'axios'
import Component from '../components'
import {Link} from 'react-router-dom'
import React,{useState} from 'react'
const {REACT_APP_API_URL} = process.env

const Login = () =>{
  const [formData, setFormData] = useState({
    email: '-',
    password: '-',
  })
  const [userToken, setUserToken] = React.useState(null)

  const processLogin = ()=>{
    axios({
      method: 'POST',
      url: `${REACT_APP_API_URL}users/login`,
      data: formData
    }).then((res)=> {
      console.log(res.data)
      localStorage.setItem('userToken', res.data.token)
      setUserToken(res.data.token)
      // history.replace('/')
    }).catch((err)=> {
      console.log(err)
    })
  }
  localStorage.getItem('userToken')
  return(
    <section className="container-fluid">
      <div className="row justify-content-center">
        <form className="auth card bg-white p-5 shadow-sm col-md-4" onClick={(e)=> processLogin(e)}>
          <h4 className="text-center text-purple fw-bold">Login</h4>
          <span className="text-start mt-4 mb-3 fw-bold">Hi, Welcome back!</span>
          <div className="my-1">
            <label htmlFor="inputEmail" className="form-text">Email</label>
            <input type="email" className="form-control input-line" id="inputEmail" name="inputEmail" onChange={(e) => {
                setFormData({ ...formData, email: e.target.value })
            }} />
          </div>
          <div className="my-1">
            <label htmlFor="inputPassword" className="form-text">Password</label>
            <input type="password" className="input-line form-control password" name="inputPassword" id="inputPassword" onChange={(e) => {
                setFormData({ ...formData, password: e.target.value })
            }}/>
            <button className="btn-lg toogle-btn align-content-md-end" name="toogle"><img src={i_eye} alt="..." /></button>
          </div>
          <Link to='./ForgotPassword' className='text-end mb-3 text-purple'>
            <span>Forgot Password ?</span>
          </Link>
          <Component.buttonPurple text='Login'  onClick={(e)=> processLogin(e)}/>
          <span className="text-muted text-center label">Login with</span>
          <Component.buttonWhite text=' Google' image ={i_google} />
          <span className="mb-1 text-center">Don't have account ?
          <Link to='./Register'  className='text-purple fw-bold'>
            <span>Sign Up</span>
          </Link>
          </span>
        </form>
      </div>
    </section>
  )
}

export default Login
