import {
    useLocation
} from "react-router-dom";
import { i_camera, i_contact, i_document, i_emoticon, i_image, i_location, i_plus, i_profileMenu } from "../../../images";
import Avatar from '../../../images/photos'
import axios from 'axios'
import { useState } from "react";
function useQuery() {
    return new URLSearchParams(useLocation().search);
}
const Message = () =>{
    let query = useQuery()

    if(query.get("id")==null){
        return(
            <div className="splashscreen">
                <span className="text-muted">Please select a chat to start messaging</span>
            </div>
        )
    }else{
        const [user, setUser] = useState([])
        React.useEffect(()=>{
            axios.get('http://localhost:5024/api/v1/contacts?id=1').then((res) => {
                setUser(res.data.data)
            }).catch((err) => {
                console.log(err.message)
            })
        },[])
        return(
            <div className="head-list bg-white">
                <div className="head-list bg-white">
                    <div className="row m-0" onclick="call()">
                        <div className="col-2 col-sm-2 col-md-2 col-lg-1 col-xl-1 mt-2 ms-2">
                            <img src={Avatar.photo7} className="img-fluid" alt="..." />
                        </div>
                        <div className="col-8 col-sm-8 col-md-8 col-lg-9 col-xl-10 mt-3">
                            <span className="fw-bold">{query.get('username')}</span><br />
                            <small className="text-purple">Online</small>
                        </div>
                        <div className="col-auto col-sm-auto col-md-auto col-lg-auto col-xl-auto mt-4">
                            <img src={i_profileMenu} alt="..." />
                        </div>
                    </div>
                </div>

                <div className="list-message bg-light d-block">

                </div>

                <div className="footer-list bg-white d-block">
                    <div className="position-relative input-group p-3">
                        <input type="text" className="form-control search-box icon bg-light " placeholder="Type your message..." aria-label="Username" aria-describedby="basic-addon1" />
                        <img src={i_plus} className="input-group-text search-box bg-light img-fluid" alt="..." />
                        <img src={i_emoticon} className="input-group-text search-box bg-light img-fluid" alt="..." />
                        <img src={i_camera} className="input-group-text search-box field bg-light img-fluid" alt="..." />
                    </div>
                    <div className="d-none d-sm-none d-md-block position-absolute col-lg-2 col-md-3 col-sm-4 col-8 g-2 bottom-0 end-0 pb-0 m-5 pe-5">
                        <div className="shadow-sm p-3 bg-purple menu-panel-right align-middle mt-5 me-5">
                            <div className="list-group">
                                <div className="d-flex justify-content-start mb-1">
                                    <img src={i_image} alt="..." width="13px" />
                                    <smal className="text-white ms-2">Image</smal>
                                </div>
                                <div className="d-flex justify-content-start mb-1">
                                    <img src={i_document} alt="..." width="13px" />
                                    <smal className="text-white ms-2">Document</smal>
                                </div>
                                <div className="d-flex justify-content-start mb-1">
                                    <img src={i_contact} alt="..." width="13px" />
                                    <smal className="text-white ms-2">Contact</smal>
                                </div>
                                <div className="d-flex justify-content-start mb-1">
                                    <img src={i_location} alt="..." width="13px" />
                                    <smal className="text-white ms-2">Location</smal>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Message
