import axios from 'axios'
import React,{useState} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { NewMessage } from '../../../../../Redux/Actions/chatlist';
const {REACT_APP_API_URL, REACT_APP_API_IMAGE_URL} = process.env

let LastMessage = (props) =>{
    let id_chatroom = props.id_chat
    const dispatch = useDispatch()
    const { data:user } = useSelector((state)=> state.UserLogin)
    const { data:message , error, loading } = useSelector((state) => state.NewMessage)
    React.useEffect(()=>{
        dispatch(NewMessage(user.data.token,id_chatroom))
    },[message])
    /* React.useEffect(()=>{
        axios.get(`${REACT_APP_API_URL}chats/?id_chatroom=${id_chatroom}`,{
            headers: {
                'user-token': localStorage.getItem('userToken')
            }
        }).then((res)=>{
            SetMessage(res.data.data)
        }).catch((err)=>{
            console.log(`ini error ${err}`)
        })
    }) */
    return(
        <div className="row">
        {   message.data &&
            message.data.map((element)=>(
        <div className="d-flex inline">
            <div className="col-9 col-lg-9 mt-1">
                <div className="d-flex flex-row">
                    {(element.status == 'unread') ? <small className="text-purple text-sm-start">{element.message}</small> : <small className="text-muted text-sm-start">{element.message}</small>}
                </div>
            </div>
            <div className="col-3 col-lg-3 mt-2">
                <div className="d-flex flex-column bd-highlight">
                    3
                </div>
            </div>
        </div>
        ))
        }
        </div>
    )
}

export default LastMessage
