import LastMessage from './lastMessage'
import { useSelector, useDispatch } from 'react-redux';
import { ChatList } from '../../../../../Redux/Actions/chatlist';
const {REACT_APP_API_URL, REACT_APP_API_IMAGE_URL} = process.env

const List = () =>{
    const { data:user } = useSelector((state)=> state.LoginUser)
    const { data, error, loading } = useSelector((state) => state.ChatList)
    let [updated, SetUpdated] = useState([])
    let id = localStorage.getItem('user_id');
    const dispatch = useDispatch()
    
    React.useEffect(()=>{
        dispatch(ChatList("eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6NSwiZW1haWwiOiJpcnZhbmp1bmFpZGk5OUBnbWFpbC5jb20iLCJ0eXBlIjoiYmFzaWMiLCJpYXQiOjE2MTkwMDY1Nzh9.tGSd3l4sSnfpv4qYX4iKPlZLGCiLLl-gB-XIIxpQLVE",id_user))
    })
    return(
        <div className="list mb-5">
            {data.map((element)=>(
            <Link to={`/?id=${element.id_user2}`}  className='no-styling'>
                <div className="d-flex flex-row row my-3 me-0">
                    <div className="col-4 col-lg-4">
                        <img src={`${REACT_APP_API_IMAGE_URL}${element.photo}`} className="photo" alt="..." />
                    </div>
                    <div className="col-8 col-lg-8">
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-2">
                                <div className="d-flex flex-row">
                                    <span className="fw-bold text-lg-start">{element.name ?? element.username} 
                                       
                                    </span>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    <small className="text-muted text-lg-start mb-1"></small>
                                </div>
                            </div>
                        </div>
                        <LastMessage id_chat={element.chatroom}/>
                    </div>
                </div>
            </Link>
            )
            )}
      </div>
    )
}

export default List
