import {
  BrowserRouter as Router, //router atau jalan ke arah mana
  Switch, // cabang antar halaman
  Route,
  Link
} from "react-router-dom";
import pages from './pages'

const{Login, Register, ForgotPassword, SplashScreen} = pages
export default function App() {
  return (
    <Router>
      <div className='bg-light'>
      <Switch></Switch>
          <Route path="/register">
            <Register />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/forgotpassword">
            <ForgotPassword />
          </Route>
          <Route exact path="/">
            <SplashScreen />
          </Route>
      </div>
    </Router>
  );
}

function Home() {
  return <h2>Home</h2>;
}

function About() {
  return <h2>About</h2>;
}

function Users() {
  return <h2>Users</h2>;
}
