    let [message, SetMesssage] = useState([])
    let id_chatroom = props.id_chat
    React.useEffect(()=>{
        axios.get(`${REACT_APP_API_URL}chats/?${id_chatroom}`,{
            headers: {
                'user-token': localStorage.getItem('userToken')
            }
        }).then((res)=>{
            return setMessage(res.data.data)
        }).catch((err)=>{
            console.log(`ini error ${err}`)
        })
    })
    return(
    <div className="col-9 col-lg-9 mt-1">
        <div className="d-flex flex-row">
        {message.map((element)=>(
            <small className="text-purple text-sm-start">{element.messsage}</small>
        ))
        }
        </div>
    </div>
    )
}

