import { Link,useParams } from 'react-router-dom'
import axios from 'axios'
import React,{useState} from 'react'
const {REACT_APP_API_URL, REACT_APP_API_IMAGE_URL} = process.env

const List = () =>{
    // state = {
    let [data, setData] = useState([])
    let [message, setMessage] = useState([])
    let [updated, SetUpdated] = useState([])
    let id = localStorage.getItem('user_id');
    // }
    React.useEffect(()=>{
        axios.get(`${REACT_APP_API_URL}chats/${id}`,{
            headers: {
                'user-token': localStorage.getItem('userToken')
            }
        }).then((res) => {
            setData(res.data.data)
            console.log(res.data.data)
        }).catch((err) => {
            console.log(`ini error ${err}`)
        })
    },[updated])

    let getLast = (id_chatroom)=>{
    
            axios.get(`${REACT_APP_API_URL}chats/?${id_chatroom}`,{
                headers: {
                    'user-token': localStorage.getItem('userToken')
                }
            }).then((res)=>{
                setMessage(res.data.data)
                console.log(res.data.data)
            }).catch((err)=>{
                console.log(`ini error ${err}`)
            })
        
    }

    return(
        <div className="list mb-5">
            {data.map((element)=>(
            <Link to={`../${id}?id=${element.id_user2}`}  className='no-styling'>
                <div className="d-flex flex-row row my-3 me-0">
                
                    <div className="col-4 col-lg-4">
                        <img src={`${REACT_APP_API_IMAGE_URL}${element.photo}`} className="photo" alt="..." />
                    </div>

                    <div className="col-8 col-lg-8">
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-2">
                                <div className="d-flex flex-row">
                                    <span className="fw-bold text-lg-start">{element.name} 
                                       
                                    </span>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    <small className="text-muted text-lg-start mb-1"></small>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-1">
                                <div className="d-flex flex-row">
                                    <small className="text-purple text-sm-start">{getLast(element.id_chatroom)}</small>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Link>
            )
            )}
      </div>
    )
}

export default List
