import { Link } from 'react-router-dom'
import axios from 'axios'
import React,{useState} from 'react'


const List = () =>{
    // state = {
    let [data, setData] = useState([])
    // }
    React.useEffect(()=>{
        /* axios.get('http://localhost:5024/api/v1/contacts?id=1').then((res) => { */
        axios.get('http://localhost:5024/api/v1/chats/1').then((res) => {
            setData(res.data.data)
        }).catch((err) => {
            console.log(`ini error ${err.message}`)
        })
    },[])
    console.log(data)
    return(
        <div className="list mb-5">
            {data.map((element)=>(
            <Link to={`../chat?id=${element.friend}`}  className='no-styling'>
                <div className="d-flex flex-row row my-3 me-0">
                    <div className="col-4 col-lg-4">
                        <img src={`http://localhost:5024/${element.image}`} className="photo" alt="..." />
                    </div>
                    <div className="col-8 col-lg-8">
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-2">
                                <div className="d-flex flex-row">
                                    <span className="fw-bold text-lg-start">{element.friend} 
                                       
                                    </span>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    <small className="text-muted text-lg-start mb-1"></small>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-1">
                                <div className="d-flex flex-row">
                                    <small className="text-purple text-sm-start"></small>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Link>
            )
            )}
      </div>
    )
}

export default List
