import {List, Filter, Menu, Message} from './Component'
import React,{useState, useEffect} from 'react'

const Chatlist=()=>{
    const [visible, setVisible] = useState(false);

    useEffect(()=>{
        let component = document.getElementById("sidebar");
        if(visible){
            console.log('block')
            component.classList.add("d-block");
            component.classList.remove("d-none");
        }else{
            console.log('none')
            component.classList.add("d-none");
            component.classList.remove("d-block");
        }
    },[visible])

    return (
        <section className="container-fluid">
            <div className="row">
                <aside className="col-sm-5 d-block col-md-5 col-lg-3 px-4 bg-white position-relative">
                    <div className="d-flex align-items-center mt-4 mb-3">
                        <span className="text-purple flex-grow-1 text-start" id="chatify"><h4>Chatify</h4></span>
                        <div className="ps-0 float-md-end"><img src={process.env.PUBLIC_URL + '/images/i_menu.svg'} alt="..." onClick={()=> visible ? setVisible(false) : setVisible(true)}/></div>
                        <div className="flex-grow-1 position-absolute w-100" id='sidebar'>
                            <div className="d-flex justify-content-between p-3 message-right bg-purple float-end">
                                <a href="#news"><img src="style/images/add.svg" className="img-fluid" alt="..." /></a>
                                <a href="#news"><img src="style/images/Lock.svg" className="img-fluid" alt="..." /></a>
                                <a href="#news"><img src="style/images/channel.svg" className="img-fluid" alt="..." /></a>
                            </div>
                        </div>
                       {/*  <Menu states={visible}/> */}
                    </div>
                    <div className="d-flex align-items-center m-1">
                        <div className="input-group w-100 h-100">
                            <img src={process.env.PUBLIC_URL + '/images/i_search.svg'} className="input-group-text search-box icon bg-light img-fluid" id="basic-addon1" alt="..." />
                            <input type="text" className="form-control search-box field bg-light " placeholder="Type your message..." aria-label="Username" aria-describedby="basic-addon1" />
                        </div>
                        <div className="ms-3 float-md-end"><img src={process.env.PUBLIC_URL + '/images/i_plus.svg'} alt="..." /></div>
                    </div>
                    <Filter/>
                    <List />
                </aside>
                <main className="d-none d-sm-block col-sm-7 col-md-7 col-lg-9 col-xs-9 p-0">
                    <Message/>
                </main>
            </div>
        </section>
    )
}

export default Chatlist
