import React,{useState,useEffect}from 'react'
import { Redirect, Router, Switch } from 'react-router';
import {i_splash} from '../images'

const Splashscreen = () =>{
    const [count, setCount] = useState(0);

    useEffect(() => {
     const timeout = setTimeout(() => {
        setCount(1);
      }, 3000);
    },[]);
    if(count > 0){
        return(
            <Switch>
                <Router>
                    <Redirect to='/login' />
                </Router>
            </Switch>
        )
    }
    return(
        <div className='bg-purple'>
            <section className="container splashscreen">
                <img src={i_splash} className="rounded mx-auto d-block" alt="..." />
            </section>
        </div>
    )
}

export default Splashscreen
