import {
  BrowserRouter as Router, //router atau jalan ke arah mana
  Switch, // cabang antar halaman
  Route,
  /* Link,
  useLocation */
} from "react-router-dom";
import pages from './pages'
import ChatPage from './pages/chat_layout'
const{Chatlist, callHistory} = ChatPage
const{Login, Register, ForgotPassword, SplashScreen, NoPageFound} = pages
export default function App() {
  /* const [count, setCount] = useState(0);
    useEffect(() => {
     const timeout = setTimeout(() => {
        setCount(1);
      }, 3000);
    },[]); */
  return (
    <Router>
      <div className='bg-light'>
        <Switch>
            <Route exact path="/" component={SplashScreen}/>
            <Route path="/register" component={Register} />
            <Route path="/login" component={Login}/>
            <Route path="/forgotpassword" component={ForgotPassword}/>
            <Route path="/chat" component={Chatlist}/>
            <Route path="/callhistory" component={callHistory}/>
            <Route component={NoPageFound} />
        </Switch>
      </div>
    </Router>
  );
}
