import axios from 'axios'
const {REACT_APP_API_URL} = process.env

const LoginRequest = () =>{
    return{
        type : 'LOGIN_REQUEST'
    }
}

const LoginSuccess = (data) => {
    return{
        type : 'LOGIN_SUCCESS',
        payload : data
    }
}

const LoginError = (err) =>{
    return{
        type : 'LOGIN_ERROR',
        payload : err
    }
}

export const LoginUser = (formData, cb)=>{
    return (dispatch)=>{
        cb(true)
        return axios({
            method: 'POST',
            url: `${REACT_APP_API_URL}users/login`,
            data: formData,
        }).then((res)=>{
            dispatch(LoginSuccess(res.data.data.token))
            cb(false)
        }).catch((err)=>{
            LoginError(err)
            cb(false)
        })
    }
}
