import { Link } from 'react-router-dom'
import { i_bell } from '../../../images'
import Avatar from '../../../images/photos'
import axios from 'axios'
import React,{useState} from 'react'


const List = () =>{
    // state = {
    const [data, setData] = useState(null)
    // }
    React.useEffect(()=>{
        axios.get('http://localhost:5024/api/v1/contacts?id=1').then((res) => {
           /*  res.data.data.map((element)=>{
                console.log(element.name)
            }) */
            setData(res.data.data)
            /* console.log('ini')
            setDataFriend(res.dataFriend) */
        }).catch((err) => {
            console.log(err.message)
        })
    })
    const dataA =[
        {
            username : 'Theresa Webb' ,
            photo : Avatar.photo3,
            time : '15:20',
            newMessage :<small className="text-purple text-sm-start">Why did you do that?</small>,
            icon :  <img src={i_bell} class="img-fluid" alt="..."/>,
            count : <span className="bg-purple-icon">2</span>,
            id :1
        },
        {
            username : 'Brother' ,
            photo : Avatar.photo4,
            time : 'Yesterday',
            newMessage : <small className="text-purple text-sm-start">Ok. Good bye!</small>,
            icon :  null,
            count : null,
            id:2
        },
        {
            username : 'Theresa Webb' ,
            photo : Avatar.photo5,
            time : '15:20',
            newMessage : <small className="text-purple text-sm-start">Hi, bro! Come to my house!</small>,
            icon :  null,
            count : <span className="bg-purple-icon">1</span>,
            id:3
        },
        {
            username : 'Theresa Webb' ,
            photo : Avatar.photo6,
            time : '15:20',
            newMessage : <small className="text-muted text-sm-start">Me: Bro, just fuck off</small>,
            icon :  null,
            count : null,
            id:4
        },
        {
            username : 'Mother ❤' ,
            photo : Avatar.photo7,
            time : '15:20',
            newMessage : <small className="text-muted text-sm-start">Me: Yes, of course come, ...</small>,
            count : null,
            icon :  null,
            id:5
        },
        
    ]

    return(
        <div className="list mb-5">
            {dataA.map((element)=>(
            <Link to={`/:username/chat?id=${element.id}&username=${element.username}&photo=${element.photo}`}  className='no-styling'>
                <div className="d-flex flex-row row my-3 me-0">
                    <div className="col-4 col-lg-4">
                        <img src={element.photo} className="img-fluid" alt="..." />
                    </div>
                    <div className="col-8 col-lg-8">
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-2">
                                <div className="d-flex flex-row">
                                    <span className="fw-bold text-lg-start">{element.username} 
                                        {element.icon}
                                    </span>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    <small className="text-muted text-lg-start mb-1">{element.time}</small>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-1">
                                <div className="d-flex flex-row">
                                    <small className="text-purple text-sm-start">{element.newMessage}</small>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    {element.count}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Link>
            )
            )}
      </div>
    )
}

export default List
