import { Link,useParams } from 'react-router-dom'
import axios from 'axios'
import React,{useState} from 'react'
import LastMessage from './lastMessage'
import { useSelector, useDispatch } from 'react-redux';
import { ChatList,NewMessage } from '../../../../../Redux/Actions/chatlist';
const {REACT_APP_API_URL, REACT_APP_API_IMAGE_URL} = process.env

const List = () =>{
    const [chatroom, SetChatRoom] = useState()
    const dispatch = useDispatch()
    const { data:user } = useSelector((state)=> state.UserLogin)
   
    const { data:chatlist, error:chatlistError, loading:loadingError } = useSelector((state) => state.ChatList)
    const { data:lastmessage, error:lastmessageError, loading:lastmessageLoading } = useSelector((state) => state.NewMessage)
    React.useEffect(()=>{
        dispatch(ChatList(user.data.token,user.data.id_user))
        SetChatRoom(chatlist.data.chatroom)
    },[])
    React.useEffect(()=>{
        dispatch(NewMessage(user.data.token, chatlist.data.chatroom))
    },[])
   
    return(
        <div className="list mb-5">
            {
                chatlist.data && 
                chatlist.data.map((element)=>(
            <Link to={`/?id=${element.id_user2}`}  className='no-styling'>
                <div className="d-flex flex-row row my-3 me-0">
                    <div className="col-4 col-lg-4">
                        <img src={`${REACT_APP_API_IMAGE_URL}${element.photo}`} className="photo" alt="..." />
                    </div>
                    <div className="col-8 col-lg-8">
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-2">
                                <div className="d-flex flex-row">
                                    <span className="fw-bold text-lg-start">{element.name ?? element.username} 
                                       
                                    </span>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    <small className="text-muted text-lg-start mb-1"></small>
                                </div>
                            </div>
                        </div>
                        <LastMessage data={lastmessage}/>
                    </div>
                </div>
            </Link>
            )
            )}
      </div>
    )
}

export default List
