const LoginUser = (state = initialState, action = {}) => {
    switch(action.type){
    case 'LOGIN_REQUEST':
        return{
            ...state,
            loading : true
        }
    case 'LOGIN_SUCCESS':
        return{
            ...state,
            loading : false,
            isLogin : true,
            data : action.payload
        }
    case 'LOGIN_ERROR' :
        return{
            ...state,
            loading : false,
            isLogin : false,
            error : action.payload,
            data : []
        }
    case 'USER_LOGOUT':
        return{
            ...state
        }
    default :
        return state
    }
}
export {LoginUser}
