const initialState = {
    data : [],
    loading : false
}

const FetchUsers = (state = initialState, action = {}) => {
    switch(action.type){
    case 'USERS_REQUEST':
        return{
            ...state,
            loading : true
        }
    case 'USERS_SUCCESS':
        return{
            ...state,
            loading : false,
            data : action.payload
        }
    case 'USERS_ERROR' :
        return{
            ...state,
            loading : false,
            error : action.payload,
            data : []
        }
    default :
        return state
    }
}

const LoginUser = (state = initialState, action = {}) => {
    switch(action.type){
    case 'LOGIN_REQUEST':
        return{
            ...state,
            loading : true
        }
    case 'LOGIN_SUCCESS':
        return{
            ...state,
            loading : false,
            data : action.payload
        }
    case 'LOGIN_ERROR' :
        return{
            ...state,
            loading : false,
            error : action.payload,
            data : []
        }
    default :
        return state
    }
}
export {LoginUser}
