import { Link,useParams } from 'react-router-dom'
import axios from 'axios'
import React,{useState} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { ChatList,NewMessage } from '../../../../../Redux/Actions/chatlist';
import Moment from 'react-moment';

const {REACT_APP_API_URL, REACT_APP_API_IMAGE_URL} = process.env
const List = () =>{
    const dispatch = useDispatch()
    const { data:user } = useSelector((state)=> state.UserLogin)
    React.useEffect(()=>{
        dispatch(ChatList(user.data.token,user.data.id_user))
    },[])
    React.useEffect(()=>{
        dispatch(NewMessage(user.data.token,user.data.id_user))
    },[])
    const { data:chatlist, error, loading } = useSelector((state) => state.ChatList)
    return(
        <div className="list mb-5">
            {
                chatlist.data && 
                chatlist.data.map((element)=>(
            <Link to={`/?id=${element.chatroom}`}  className='no-styling'>
                <div className="d-flex flex-row row my-3 me-0">
                    <div className="col-5 col-md-5 col-lg-5">
                        <img src={`${REACT_APP_API_IMAGE_URL}${element.photo}`} className="photo" alt="..." />
                    </div>
                    <div className="col-7 col-md-7 col-lg-7">
                        <div className="row">
                            <div className="col-9 col-lg-9 mt-2">
                                <div className="d-flex flex-row">
                                    <span className="fw-bold text-lg-start">{element.name ?? element.username} 
                                    </span>
                                </div>
                            </div>
                            <div className="col-3 col-lg-3 mt-2">
                                <div className="d-flex flex-column bd-highlight">
                                    <small className="text-muted text-lg-start mb-1"><Moment fromNow>{element.message_timestamp}</Moment></small>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="d-flex inline">
                                <div className="col-9 col-lg-9 mt-1">
                                    <div className="d-flex flex-row">
                                        {(element.status == 'unread') ? <small className="text-purple text-sm-start">{element.message}</small> : <small className="text-muted text-sm-start">{element.message}</small>}
                                    </div>
                                </div>
                                <div className="col-3 col-lg-3 mt-2">
                                    <div className="d-flex flex-column bd-highlight">
                                        3
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Link>
            )
            )}
      </div>
    )
}

export default List
