import {
  BrowserRouter as Router, //router atau jalan ke arah mana
  Switch, // cabang antar halaman
  Route,
  /* Link,
  useLocation */
} from "react-router-dom";
import pages from './pages'
import ChatPage from './pages/chat_layout'
import React,{useState} from 'react'
import { PrivateRoute, PublicRoute} from "./components";
const{Chatlist, callHistory} = ChatPage
const{Login, Register, ForgotPassword, SplashScreen, NoPageFound} = pages


export default function App() {
  return(
    <Router>
      <Switch>
        <PublicRoute path="/login" restricted ={true} exact={true} component={()=><Login />} />
        <PublicRoute path="/register" restricted = {true} exact={true} component={()=><Register/>} />
        <PublicRoute path="/forgot_password" restricted = {true} exact={true} component={()=><ForgotPassword/>} />
        <PrivateRoute path="/" exact={true} component={()=><Chatlist />} />
      </Switch>
    </Router>
  )
}
