import {
  BrowserRouter as Router, //router atau jalan ke arah mana
  Switch, // cabang antar halaman
  Route,
  /* Link,
  useLocation */
} from "react-router-dom";
import pages from './pages'
import ChatPage from './pages/chat_layout'
import React,{useState} from 'react'
import { PrivateRoute, PublicRoute} from "./components";
import splashscreen from "./pages/SplashScreen";
const{Chatlist, callHistory} = ChatPage
const{Login, Register, ForgotPassword, SplashScreen, NoPageFound} = pages


export default function App() {
  const [userToken, setUserToken] = useState(null)

  React.useEffect(() => {
    async function getToken() {
      const userToken = await localStorage.getItem('userToken')
      setUserToken(userToken)
    }
    getToken();
  }, [userToken])
  return(
    <Router>
      <Switch>
        <PrivateRoute path="/"  component={()=><Chatlist/>} />
        <PrivateRoute path="/chat_list"  component={()=><SplashScreen/>} />
        <PrivateRoute path="/contacts"  component={()=><Chatlist/>} />
        <PublicRoute path="/login" restricted = {true} exact={true} component={()=><Login nama={'irvan'}/>} />
      </Switch>
    </Router>
  )
